rrentTime- 30000 ) { HTTPServer. this .ht.remove(tmp.fileName); } } } catch (InterruptedException epp) {} } } } class HTTPCon extends Thread { Queue queue; public HTTPCon (Queue q) { queue = q; } public void run () { BufferedOutputStream out = null ; BufferedReader in = null ; for (;;) { // loop forever to serve HTTP clients try { Socket s = queue.deQueue(); out = new BufferedOutputStream(s.getOutputStream()); in = new BufferedReader( new InputStreamReader(s.getInputStream())); String line; String whichFile= / ; while ((line=in.readLine())!= null &amp;&amp; !line.equals(  )) { //System.out.println(line); // dump for debugging if (line.startsWith( GET )) { // StringTokenizer&#x5C07;&#x5B57;&#x4E32;&#x4EE5;&#x7A7A;&#x767D;&#x5206;&#x958B; StringTokenizer st = new StringTokenizer(line); st.nextToken(); whichFile = st.nextToken(); break ; } } if (whichFile.equals( / )) { whichFile = /Welcome.html ; } // &#x904E;&#x6FFE;&#x6389;&#x7B2C;&#x4E00;&#x500B; &apos;/&apos; whichFile = whichFile.substring( 1 ,whichFile.length()); String fileType = text/plain ; if (whichFile.endsWith( .html )) { // HTML&#x6587;&#x5B57;&#x6A94; fileType = text/html ; } else if (whichFile.endsWith( .txt )) { // &#x6587;&#x5B57;&#x6A94; fileType = text/plain ; } else if (whichFile.endsWith( .jpg )) { // JPG&#x5716;&#x6A94; fileType = image/jpg ; } else if (whichFile.endsWith( .gif )) { // GIF&#x5716;&#x6A94; fileType = image/gif ; } // read from cache instead of file CacheData tmp; byte [] data; if ((tmp=((CacheData)HTTPServer. this .ht.get(whichFile)))!= null ) { data = tmp.data; HTTPServer. this .ht.remove(whichFile); // clear cache. Assume read once only out.write( HTTP/1.0 200 OK
 .getBytes()); out.write( Connection: close
 .getBytes()); out.write(( Content-Type:  +fileType+ 
 ).getBytes()); out.write(( Content-Length:  +data.length+ 

 ).getBytes()); out.write(data); } else { out.write( HTTP/1.0 404 &#x627E;&#x4E0D;&#x5230;&#x7269;&#x4EF6;
 .getBytes()); out.write( Content-Type: text/html

 .getBytes()); out.write( htmlbodyh1HTTP/1.0 404 &#x627E;&#x4E0D;&#x5230;&#x7269;&#x4EF6; /h1/body/html .getBytes()); } out.flush(); in.close(); out.close(); s.close(); } catch (Exception ex) { // fail to listen port //System.out.println(ex); } } } } class CacheData { String fileName; byte [] data; long time; public CacheData (String fileName, byte [] data, long time) { this .fileName = fileName; this .data = data; this .time = time; } } public String put (String extension, byte [] data) { String fileName; do { fileName = Integer.toString(( int )(Math.random()* 1000000 ))+extension; } while (ht.get(fileName)!= null ); ht.put(fileName, new CacheData(fileName,data,System.currentTimeMillis())); return fileName; } public void run () { try { ServerSocket ss = new ServerSocket( 1999 ); Queue q = new Queue(); for ( int i= 0 ; i 3 ; i++) { ( new HTTPCon(q)).start(); } ( new Cleaner()).start(); for (;;) { // loop forever to serve HTTP clients try { // &#x63A5;&#x6536;&#x65B0;&#x7684;&#x7DB2;&#x8DEF;&#x9023;&#x7DDA; Socket s = ss.accept(); s.setSoTimeout( 5000 ); // 5&#x79D2;&#x5167;&#x8981;&#x8B80;&#x5230;&#x8CC7;&#x6599; q.enQueue(s); } catch (Exception ex2) { // fail to process Socket ex2.printStackTrace(); } } } catch (Exception ex) { // fail to listen port ex.printStackTrace(); } } }

